INSERT INTO `culture` (`id`, `title`, `detail`, `pic`) VALUES
('CUL-000004', 'ศาลหลักเมืองขอนแก่น', 'ศาลหลักเมืองขอนแก่น : เป็นสถานที่เคารพบูชาของชาวขอนแก่น ประดิษฐานอยู่ที่ศาลาสุขใจ ถนนเทพารักษ์ หน้าสำนักงานเทศบาลนครขอนแก่น ท่านเจ้าคุณปู่พระราชสารธรรมมุนีและหลวงธุรนัยพินิจ อดีตผู้ว่าราชการจังหวัดขอนแก่น ได้นำหลักศิลาจารึกมาจากโบราณสถานในท้องที่อำเภอชุมแพมาประกอบพิธีตามแนวทางพระพุทธศาสนาทำเป็นหลักเมืองเมื่อวันที่ 20 สิงหาคม พ.ศ. 2499 ต่อมาเมื่อ พ.ศ. 2549 เทศบาลนครขอนแก่น ได้ทำการบูรณะศาลหลักเมืองขอนแก่น ตามโครงการบูรณะพัฒนาปฏิสังขรณ์ศาลหลักเมืองและในวโรกาสมหามงคลสมัยเพื่อน้อมถวายพระพรชัยมงคลแด่พระบาทสมเด็จพระเจ้าอยู่หัวภูมิพลอดุลยเดชมหาราชทรงเจริญพระชนมายุ 80 พรรษา โดยผลการคัดเลือกผู้รับเหมาก่อสร้างวิธีพิเศษ ได้ตกลงว่าจ้าง หจก.แก่นชาญกิจวิศวกรรม มาดำเนินการก่อสร้าง แล้วเสร็จและมีการฉลองสมโภชศาลหลักเมืองขอนแก่น เพื่อเฉลิมพระเกียรติพระบาทสมเด็จพระเจ้าอยู่หัวฯ 80 พรรษา เมื่อวันที่ 3 ธันวาคม 2550 สิ้นค่าก่อสร้างกว่า 54 ล้านบาท ศาลหลักเมืองขอนแก่นที่ปรับปรุงใหม่นั้น อยู่ ณ บริเวณจุดเดิม ลักษณะตัวอาคารมีศิลปะและสถาปัตยกรรมไทยประยุกต์ ทรวดทรงและส่วนประกอบงานศิลป์เป็นการอนุรักษ์งานสถาปัตยกรรมที่สำคัญของ ท้องถิ่นอีสาน ขนาดและรูปทรงเป็นเป็นอาคารโถงจัตุรมุข กว้างขวางโอ่โถงกว่าของเดิมมาก โดยมีขนาดตัวอาคาร 13 x 13 เมตร โครงสร้างเป็นคอนกรีตเสริมเหล็กมีพื้นที่ภายในเป็นห้องโถงรวม 73 ตารางเมตร ย่อมุมตัวอาคารโดยรอบมีระเบียงยื่นทั้ง 4 ด้าน ความสูงจากพื้นลานรอบอาคารถึงถึงยอดฉัตรทองคำรวม 27.50 เมตร หลังคาเป็นทรงจั่วจัตุรมุขหลังคาซ้อน 3 ชั้น และชั้นเครื่องยอดเป็นรูปเจดีย์ศิลปะพื้นเมืองอีสาน สัณฐานเป็นเจดีย์จำลองจากองค์พระธาตุขามแก่น', 'CUL-000004.jpg'),
('CUL-000005', 'พระมหาธาตุแก่นนคร', 'พระมหาธาตุแก่นนคร: ตั้งอยู่ภายในวัดหนองแวงพระอารามหลวง ถนนกลางเมือง เป็นศิลปะสมัยทวารวดีผสมผสานศิลปะอินโดจีน รูปทรงแบบชาวอีสานตากแห มี 9 ชั้น เป็นที่ประดิษฐานพระบรมสารีริกธาตุ มีการตกแต่งด้วยลวดลายแกะสลัก และภาพเขียนอย่างงดงาม เป็นสถานที่เคารพบูชาของชาวขอนแก่น และเป็นสถานที่ชมทัศนียภาพเมืองขอนแก่น', 'CUL-000005.jpg'),
('CUL-000006', 'อนุสาวรีย์พระนครศรีบริรักษ์', 'อนุสาวรีย์พระนครศรีบริรักษ์: ตั้งอยู่ที่สวน เจ.ซี. ถนนรอบบึง พระนครศรีบริรักษ์หรือท้าวเพี้ยเมืองแพนเป็นขุนนางเชื้อพระวงศ์กษัตริย์เวียงจันทน์ มีธิดาชื่อนางคำแว่นเป็นสนมเอกในพระบาทสมเด็จพระพุทธยอดฟ้าจุฬาโลกมหาราช ในปี พ.ศ. 2332 ท้าวเพี้ยเมืองแพนได้พาสมัครพรรคพวกประมาณ 330 คน อพยพมาอยู่ที่บ้านบึงบอน ขึ้นตรงต่อพระยานครราชสีมา ต่อมาในปี พ.ศ. 2340 พระบาทสมเด็จพระพุทธยอดฟ้าจุฬาโลกมหาราชโปรดเกล้าฯ ยกบ้านบึงบอนขึ้นเป็น \"เมืองขอนแก่น\" และยกฐานันดรศักดิ์ท้าวเพี้ยเมืองแพนขึ้นเป็น \"พระนครศรีบริรักษ์\" พ่อเมืองคนแรกของจังหวัดขอนแก่น ในปี พ.ศ. 2525 ประชาชนชาวขอนแก่น ได้ร่วมใจกันสร้างอนุสาวรีย์พระนครศรีบริรักษ์ขึ้นเพื่อเป็นอนุสรณ์และเคารพสักการะของชาวเมือง', 'CUL-000006.jpg'),
('CUL-000007', 'น้ำส่างสนามบิน', 'น้ำส่างสนามบิน: ตั้งอยู่บริเวณถนนหน้าศูนย์ราชการ ตรงข้ามโรงเรียนสนามบินด้านทิศเหนือ เป็นบ่อน้ำประวัติศาสตร์ที่มีมาก่อนที่เมืองขอนแก่นจะมีน้ำประปาบริโภค เป็นจุดรวมใจของชาวขอนแก่น เป็นแหล่งน้ำที่หล่อเลี้ยงชีวิตของชาวขอนแก่นในอดีต และให้คุณประโยชน์ที่ยิ่งใหญ่แก่ประชาชนชาวขอนแก่นมาเป็นเวลายาวนาน', 'CUL-000007.jpg'),
('CUL-000008', 'พระธาตุขามแก่น ', 'พระธาตุขามแก่น : พระธาตุขามแก่นตั้งอยู่ที่วัดเจติยภูมิ บ้านขาม หมู่ที่ 1 ตำบลบ้านขาม อำเภอน้ำพอง เป็นปูชนียสถานของจังหวัดขอนแก่น บ้านขามเคยเป็นเมืองมาตั้งแต่สมัยโบราณ เป็นเวลาประมาณ 2000 ปี ตั้งแต่ พ.ศ. 500', 'CUL-000008.jpg'),
('CUL-000009', 'บึงละเลิงหวาย อำเภอพล', 'บึงละเลิงหวาย อำเภอพล : เป็นบึงขนาดเล็กซึ่งเป็นที่ตั้งของศาลเจ้าละเลิงหวาย และเป็นที่กราบสักการะของคนไทยเชื้อสายจีน และเป็นสถานที่พักผ่อนหย่อนใจที่ได้รับความนิยมเป็นอย่างสูง', 'CUL-000009.jpg'),
('CUL-000010', 'ปราสาทเปือยน้อย', 'ปราสาทเปือยน้อย ตั้งอยู่ที่ อ.เปือยน้อย เป็นปราสาทศิลปะขอมโบราณผสมระหว่างศิลปะเขมรแบบบาปวนและแบบนครวัด สร้างขึ้นในราวพุทธศตวรรษที่ 16-17 เพื่อใช้เป็นเทวสถานในศาสนาฮินดู เป็นปราสาทที่มีความสมบูรณ์ที่สุดในเขตภาคอีสานตอนบน', 'CUL-000010.jpg'),
('CUL-000011', 'พิพิธภัณฑสถานแห่งชาติที่ 7 ขอนแก่น', 'พิพิธภัณฑสถานแห่งชาติที่ 7 ขอนแก่น : ตั้งอยู่ที่ถนนกสิกรทุ่งสร้าง เป็นสถานที่ที่เก็บรักษาและจัดแสดงโบราณวัตถุและศิลปวัตถุที่เป็นของแถบอีสานตอนเหนือ โดยรอบอาคารพิพิธภัณฑ์จัดตั้งใบเสมาหินที่ได้จาก \"เมืองฟ้าแดดสงยาง\" ไว้เป็นจำนวนมาก', 'CUL-000011.jpg'),
('CUL-000012', 'หอศิลปวัฒนธรรมและอาคารศูนย์กาญจนาภิเษก มหาวิทยาลัยขอนแก่น', 'หอศิลปวัฒนธรรมและอาคารศูนย์กาญจนาภิเษก มหาวิทยาลัยขอนแก่น : เป็นแหล่งแสดงศิลปะพื้นบ้านและเป็นสถานที่จัดแสดงนิทรรศการต่างๆ ตั้งอยู่ที่ถนนมะลิวัลย์ มหาวิทยาลัยขอนแก่น', 'CUL-000012.jpg'),
('CUL-000013', 'โฮงมูนมังเมืองขอนแก่น', 'โฮงมูนมังเมืองขอนแก่น : โฮงมูนมังเมืองขอนแก่น ตั้งอยู่ ณ บริเวณชั้นล่างอาคารสวนสาธารณะ 200 ปี บึงแก่นนคร เทศบาลนครขอนแก่น ภายในโฮงมูนมันเมืองขอนแก่น ได้จัดแบ่งพื้นที่ออกเป็นพื้นที่ในส่วนของนิทรรศการ และห้องจำหน่ายของที่ระลึก สำหรับการจัดนิทรรศการได้แบ่งออกเป็น 5 โซน โดยแบ่งตามเนื้อหาสาระในระบบการปกครอง วิถีชีวิต ภูมิปัญญา และเอกลักษณ์ทางวัฒนธรรมอันน่าภาคภูมิใจของชาวขอนแก่น นับตั้งแต่ยุคก่อนประวัติศาสตร์ ยุคสร้างบ้านแปงเมือง จวบจนเป็นเมืองขอนแก่นในปัจจุบัน', 'CUL-000013.jpg');

